module Quasar.Wayland.Protocol (
  -- * Wayland wire protocol
  -- | This module exports everything required to implement Wayland interfaces generated by
  -- "Quasar.Wayland.Protocol.TH".

  -- ** Wire types
  ObjectId,
  GenericObjectId,
  NewId,
  Fixed(..),
  WlString(..),

  -- ** Classes for generated interfaces
  IsInterface(InterfaceName),
  interfaceName,
  Side(..),
  IsSide(WireUp, WireDown),
  IsInterfaceSide,

  -- ** Protocol execution
  ProtocolHandle,
  initializeProtocol,
  feedInput,
  takeOutbox,
  setException,

  -- ** Low-level protocol interaction
  ProtocolM,
  runProtocolTransaction,
  runProtocolM,
  Object,
  newObject,
  sendMessage,

  WireCallback(..),
  internalFnWireCallback,
  traceWireCallback,
  ignoreMessage,

  -- * Protocol exceptions
  WireCallbackFailed(..),
  ParserFailed(..),
  ProtocolException(..),
  MaximumIdReached(..),
  ServerError(..),
) where

import Quasar.Wayland.Protocol.Core
